import axios from 'axios';
import React from 'react';
import { useNavigate, useParams } from 'react-router-dom';
// import videoPlayImg from '../assets/img/play.png';
// import ReactPlayer from 'react-player/youtube';

const FullPizza: React.FC = () => {
  const [pizza, setPizza] = React.useState<{
      imageUrl: string;
      title: string;
      price: number;
      components: string;
      text: string;
      videoUrl: string
    }>();
  const { id } = useParams();
  const navigate = useNavigate();
 

  React.useEffect(() => {
    async function fetchPizzas() {
      try {
        const { data } = await axios.get('https://63332597433198e79dc04b5e.mockapi.io/items/' + id);
        setPizza(data);
      } catch (error) {
        alert('Помилка при завантаженні товару');
        navigate('/');
      }
    }

    fetchPizzas();
  }, []);

  if (!pizza) {
    return <>Загрузка...</>
  }
//  const [open, setOpen] = React.useState(false);
//   const Modal: React.FC = ({ open, setOpen, children }) => (
//     <div className={`overlay animated ${open ? 'show' : ''}`}>
//       <div className="modal">
//         <svg onClick={() => setOpen(false)} height="200" viewBox="0 0 200 200" width="200">
//           <title />
