      price: number;
      components: string;
      text: string;
      videoUrl: string
    }>();
  const { id } = useParams();
  const navigate = useNavigate();
 
  React.useEffect(() => {
    async function fetchPizzas() {
      try {
        const { data } = await axios.get('https://63332597433198e79dc04b5e.mockapi.io/items/' + id);
        setPizza(data);
      } catch (error) {
        alert('Помилка при завантаженні товару');
        navigate('/');
      }
    }

    fetchPizzas();
  }, []);

