import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';

const fetchPizzas = createAsyncThunk('pizza/fetchPizzasStatus', async () => {
  const { data } = await axios.get(
    `https://63332597433198e79dc04b5e.mockapi.io/items?page=${currentPage}&limit=4&${category}&sortBy=${sortBy}&order=${order}${search}`,
  );
  return data;
});

const initialState = {
  items: [],
};

const pizzaSlice = createSlice({
  name: 'pizza',
  initialState,
  reducers: {
    setItems(state, action) {
      state.items = state.payload;
    },
  },
});

export const { setItems } = pizzaSlice.actions;

export default pizzaSlice.reducer;
