import axios from 'axios';
import { createAsyncThunk, createSlice, PayloadAction } from '@reduxjs/toolkit';
import { RootState } from '../store';
import { CartItem } from './cartSlice';

export const fetchPizzas = createAsyncThunk<CartItem[], Record<string, string>>(
  'pizza/fetchPizzasStatus', 
  async (params) => { 
  const { sortBy, order, category, search, currentPage } = params;
  const { data } = await axios.get<CartItem[]>(
    `https://63332597433198e79dc04b5e.mockapi.io/items?page=${currentPage}&limit=100&${category}&sortBy=${sortBy}&order=${order}${search}`,
  );

  return data 
});

type Pizza = {
    id: string,
    title: string,
    price: number,
    imageUrl: string,
    videoUrl: string,
    sizes: number[],
    types: number[],
  };


interface PizzaSliceState  {
  items: Pizza[];
  status: 'loading' | 'succes' | 'error'
}

const initialState: PizzaSliceState = {
  items: [],
  status: 'loading', // loading | succes | error
};

const pizzaSlice = createSlice({
  name: 'pizza',
  initialState,
  reducers: {
    setItems(state, action) {
      state.items = action.payload;
    },
  },

  extraReducers: (builder) => {
    builder.addCase(fetchPizzas.pending, (state, action) => {
      state.status = 'loading';
      state.items = [];
    })
  }
  // extraReducers: {
  //   [fetchPizzas.pending]: (state) => {
  //     state.status = 'loading';
  //     state.items = [];
  //   },
  //   [fetchPizzas.fulfilled]: (state, action: PayloadAction) => {
  //     state.items = action.payload;
  //     state.status = 'succes';
  //   },
  //   [fetchPizzas.rejected]: (state, action: PayloadAction) => {
  //     state.status = 'error';
  //     state.items = [];
  //   },
  // },
});

export const selectPizzaData = (state: RootState) => state.pizza;

export const { setItems } = pizzaSlice.actions;

export default pizzaSlice.reducer;
