import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  items: [],
};

const pizzaSlice = createSlice({
  name: 'pizza',
  initialState,
  reducers: {
    setItems(state, action) {
      state.items = state.payload;
    },
  },
});

export const { srtItems } = pizzaSlice.action;

export default createSlice.reducer;
