import axios from 'axios';
import React, { useState } from 'react';
import ReactPlayer from 'react-player/youtube';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import videoPlayImg from '../assets/img/play.png';
import { selectCartItemById } from '../redux/slices/cartSlice';

const FullPizza = () => {
  const [open, setOpen] = React.useState(false);
  const [pizza, setPizza] = useState();
  const { id } = useParams();
  const addedCount = cartItem ? cartItem.count : 0;
  const cartItem = useSelector(selectCartItemById(id));
  const dispatch = useDispatch();
  const [activeType, setActiveType] = React.useState(0);
  const [activeSize, setActiveSize] = React.useState(0);

  const onClickAdd = () => {
    const item = {
      id,
      title,
      price,
      imageUrl,
      videoUrl,
      type: typeNames[activeType],
      size: sizes[activeSize],
    };
    dispatch(addItem(item));
  };

  React.useEffect(() => {
    async function fetchPizzas() {
      try {
        const { data } = await axios.get('https://63332597433198e79dc04b5e.mockapi.io/items/' + id);
        setPizza(data);
      } catch (error) {
        alert('Помилка при завантаженні товару');
      }
    }

    fetchPizzas();
  }, []);

  if (!pizza) {
    return 'Loading......';
  }

  const Modal = ({ open, setOpen, children }) => (
    <div className={`overlay animated ${open ? 'show' : ''}`}>
      <div className="modal">
        <svg onClick={() => setOpen(false)} height="200" viewBox="0 0 200 200" width="200">
          <title />
          <path d="M114,100l49-49a9.9,9.9,0,0,0-14-14L100,86,51,37A9.9,9.9,0,0,0,37,51l49,49L37,149a9.9,9.9,0,0,0,14,14l49-49,49,49a9.9,9.9,0,0,0,14-14Z" />
        </svg>
        {children}
      </div>
    </div>
  );

  return (
    <div className="container">
      <h1>{pizza.title}</h1>
      <div className="headerFull">
        <img className="imgFull" src={pizza.imageUrl} />
        <div className="componentsFull">
          <h2>описание товара </h2>
          <p>{pizza.components}</p>
        </div>
        <div className="textFull">
          <h2>характеристики товара</h2>
          <p>{pizza.text}</p>
        </div>
      </div>
      <div>
        <div className="App-modalFull">
          <li className="btnPlay" onClick={() => setOpen(true)}>
            <img className="playImg" src={videoPlayImg} alt="Empty cart" />
            <h4>Відео огляд товару</h4>
          </li>
          <Modal open={open} setOpen={setOpen}>
            <div className="video">
              <ReactPlayer url={pizza.videoUrl} />
            </div>
          </Modal>
          <button onClick={onClickAdd} className="button button--outline button--add">
            <svg
              width="12"
              height="12"
              viewBox="0 0 12 12"
              fill="none"
              xmlns="http://www.w3.org/2000/svg">
              <path
                d="M10.8 4.8H7.2V1.2C7.2 0.5373 6.6627 0 6 0C5.3373 0 4.8 0.5373 4.8 1.2V4.8H1.2C0.5373 4.8 0 5.3373 0 6C0 6.6627 0.5373 7.2 1.2 7.2H4.8V10.8C4.8 11.4627 5.3373 12 6 12C6.6627 12 7.2 11.4627 7.2 10.8V7.2H10.8C11.4627 7.2 12 6.6627 12 6C12 5.3373 11.4627 4.8 10.8 4.8Z"
                fill="white"
              />
            </svg>
            <span>Додати</span>
            {addedCount > 0 && <i>{addedCount}</i>}
          </button>
          <h1>{pizza.price}грн.</h1>
        </div>
      </div>
    </div>
  );
};

export default FullPizza;
