import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { selectSort, setSort, Sort, SortPropertyEnum } from '../redux/slices/filterSlice';



type SortItem= {
  name: string;
  sortProperty: SortPropertyEnum;
}

type PopupClick = MouseEvent & {
  path: Node[];
}

export const sortList: SortItem[]= [
  { name: '–ø–æ–ø—É–ª—è—Ä–Ω—ñ—Å—Ç—é(ü°Ö)', sortProperty: SortPropertyEnum.RATING_DESC },
  { name: '–ø–æ–ø—É–ª—è—Ä–Ω—ñ—Å—Ç—é(ü°á)', sortProperty: SortPropertyEnum.RATING_ASC },
  { name: '—Ü—ñ–Ω—ñ (ü°á)', sortProperty: SortPropertyEnum.PRICE_ASC},
  { name: '—Ü—ñ–Ω—ñ (ü°Ö)', sortProperty: SortPropertyEnum.PRICE_DESC },
  { name: '–∞–ª—Ñ–∞–≤—ñ—Ç—É (–Øü†¶–ê)', sortProperty: SortPropertyEnum.TITLE_DESC },
  { name: '–∞–ª—Ñ–∞–≤—ñ—Ç—É (–êü†¶–Ø)', sortProperty: SortPropertyEnum.TITLE_ASC },
];
const SortPopup: React.FC = () => {
  const dispatch = useDispatch();
  const sort = useSelector(selectSort);
  const sortRef = React.useRef<HTMLDivElement>(null);

  const [open, setOpen] = React.useState(false);

  const onClickListItem = (obj: SortItem) => {
    dispatch(setSort(obj));
    setOpen(false);
  };

  React.useEffect(() => {
    const handaleClickOutside = (event: MouseEvent) => {
      const _event = event as PopupClick;
      

      if (sortRef.current && !_event.path.includes(sortRef.current)) {
        setOpen(false);
      }
    };
    document.body.addEventListener('click', handaleClickOutside);

    return () => document.body.removeEventListener('click', handaleClickOutside);
  }, []);

  return (
    <div ref={sortRef} className="sort">
      <div className="sort__label">
        <svg
          width="10"
          height="6"
          viewBox="0 0 10 6"
          fill="none"
          xmlns="http://www.w3.org/2000/svg">
          <path
            d="M10 5C10 5.16927 9.93815 5.31576 9.81445 5.43945C9.69075 5.56315 9.54427 5.625 9.375 5.625H0.625C0.455729 5.625 0.309245 5.56315 0.185547 5.43945C0.061849 5.31576 0 5.16927 0 5C0 4.83073 0.061849 4.68424 0.185547 4.56055L4.56055 0.185547C4.68424 0.061849 4.83073 0 5 0C5.16927 0 5.31576 0.061849 5.43945 0.185547L9.81445 4.56055C9.93815 4.68424 10 4.83073 10 5Z"
            fill="#2C2C2C"
          />
        </svg>
        <b>–°–æ—Ä—Ç—É–≤–∞—Ç–∏ –ø–æ:</b>
        <span onClick={() => setOpen(!open)}>{sort.name}</span>
      </div>
      {open && (
        <div className="sort__popup">
          <ul>
            {sortList.map((obj, index) => (
              <li
                key={index}
                onClick={() => onClickListItem(obj)}
                className={sort.sortProperty === obj.sortProperty ? 'active' : ''}>
                {obj.name}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default SortPopup;
