import React from 'react';
import ReactPlayer from 'react-player/youtube';
import { useDispatch, useSelector } from 'react-redux';
import { addItem, CartItem, selectCartItemById } from '../../redux/slices/cartSlice';
import videoPlayImg from '../../assets/img/play.png';
import { Link } from 'react-router-dom';
import Modal from '../Modal';

const typeNames = ['Розміри', 'Ш*Г*В'];

type PizzaBlockProps = {
  id: string,
  title: string,
  price: number,
  imageUrl: string,
  videoUrl: string,
  sizes: number[],
  types: number[],
  
};

export type ModalProps = {
  open: boolean,
  setOpen: React.Dispatch<React.SetStateAction<boolean>>,
  children: any,
};

const PizzaBlock: React.FC<PizzaBlockProps> = ({
  id,
  title,
  price,
  imageUrl,
  videoUrl,
  sizes,
  types,
  
}) => {
  const dispatch = useDispatch();
  const cartItem = useSelector(selectCartItemById(id));
  const [activeType, setActiveType] = React.useState(0);
  const [activeSize, setActiveSize] = React.useState(0);

  const addedCount = cartItem ? cartItem.count : 0;

  const onClickAdd = () => {
    const item: CartItem = {
      id,
      title,
      price,
      imageUrl,
      videoUrl,
      type: typeNames[activeType],
      size: sizes[activeSize],
      count: 0,
    };
    dispatch(addItem(item));
  };

  
 

  const [open, setOpen] = React.useState(false);
  return (
    <div className="pizza-block-wrapper">
      <div className="pizza-block">
      <Link key={id} to={`/pizza/${id}`}>
        <img className="pizza-block__image" src={imageUrl} alt="Pizza" />
        <h4 className="pizza-block__title">{title}</h4>
        </Link>
      
      <div>
          <div className="App-modal">
            <li className="btnPlay" onClick={() => setOpen(true)}>
              <img className="playImg" src={videoPlayImg} alt="Empty cart" />
              <h4>Відео огляд товару</h4>
            </li>
            <Modal open={open} setOpen={setOpen}>
              <div className="video">
                <ReactPlayer url={videoUrl} />
              </div>
            </Modal>
          </div>
        </div>
        <div className="pizza-block__selector">
          <ul>
            {types.map((typeId) => (
              <li
                key={typeId}
                onClick={() => setActiveType(typeId)}
                className={activeType === typeId ? 'active' : ''}>
                {typeNames[typeId]}
              </li>
            ))}
          </ul>
          <ul>
            {sizes.map((size, index) => (
              <li
                key={index}
                onClick={() => setActiveSize(index)}
                className={activeSize === index ? 'active' : ''}>
                {size} см.
              </li>
            ))}
          </ul>
        </div>
        <div className="pizza-block__bottom">
          <div className="pizza-block__price"> {price} гр.</div>

          <button onClick={onClickAdd} className="button button--outline button--add">
            <svg
              width="12"
              height="12"
              viewBox="0 0 12 12"
              fill="none"
              xmlns="http://www.w3.org/2000/svg">
              <path
                d="M10.8 4.8H7.2V1.2C7.2 0.5373 6.6627 0 6 0C5.3373 0 4.8 0.5373 4.8 1.2V4.8H1.2C0.5373 4.8 0 5.3373 0 6C0 6.6627 0.5373 7.2 1.2 7.2H4.8V10.8C4.8 11.4627 5.3373 12 6 12C6.6627 12 7.2 11.4627 7.2 10.8V7.2H10.8C11.4627 7.2 12 6.6627 12 6C12 5.3373 11.4627 4.8 10.8 4.8Z"
                fill="white"
              />
            </svg>
            <span>Додати</span>
            {addedCount > 0 && <i>{addedCount}</i>}
          </button>
        </div>
      </div>
    </div>
  );
};

export default PizzaBlock;
